<?php

namespace Eshop\ShopBundle\Repository;

use Doctrine\DBAL\Query\QueryBuilder;
use Doctrine\ORM\EntityRepository;
use Eshop\ShopBundle\Entity\Category;
use Eshop\ShopBundle\Entity\Manufacturer;
use Eshop\UserBundle\Entity\User;

/**
 * ProductcatdeRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class ProductcatdeRepository extends EntityRepository
{
    public function getTotal()
    {
        $qb = $this->createQueryBuilder('pcde')
                   ->select('COUNT(pcde)');     // On sÃ©lectionne simplement COUNT(cu)

        return (int) $qb->getQuery()
                         ->getSingleScalarResult(); // Utilisation de getSingleScalarResult pour avoir directement le rÃ©sultat du COUNT
    } 

    public function getProductcatDistinct() 
    {
        $qb = $this->createQueryBuilder('gp')
                ->select('DISTINCT gp.cat1')
                ->where('gp.enable = :enable')
                ->setParameter('enable', 1)
                ->orderBy('gp.cat1', 'ASC')
                ->getQuery();

        return $qb;
    } 
    
}
