<?php

namespace Eshop\ShopBundle\Repository;

use Doctrine\ORM\EntityRepository;

/**
 * TagTermsRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class TagTermsRepository extends EntityRepository
{
    public function getTotal()
    {
        $qb = $this->createQueryBuilder('txt')
                   ->select('COUNT(txt)');

        return (int) $qb->getQuery()
                         ->getSingleScalarResult(); // Utilisation de getSingleScalarResult pour avoir directement le r√©sultat du COUNT
    } 
}